parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "type": {
      "type": "string",
      "enum": ["conversation", "task", "decision"],
      "description": "Type of memory: conversation (general snippets), task (action items), decision (important choices)"
    },
    "content": {
      "type": "string",
      "description": "Content to store (for conversation type)"
    },
    "session_id": {
      "type": "string",
      "description": "Session identifier (for conversation type)"
    },
    "role": {
      "type": "string",
      "enum": ["user", "assistant"],
      "description": "Message role (for conversation type)"
    },
    "message": {
      "type": "string",
      "description": "Message content (for conversation type)"
    },
    "title": {
      "type": "string",
      "description": "Title (for task/decision types)"
    },
    "description": {
      "type": "string",
      "description": "Detailed description"
    },
    "priority": {
      "type": "string",
      "enum": ["low", "medium", "high", "urgent"],
      "description": "Priority level (for task type)"
    },
    "deadline": {
      "type": "string",
      "description": "ISO date string deadline (for task type)"
    },
    "decision": {
      "type": "string",
      "description": "The decision made (for decision type)"
    },
    "rationale": {
      "type": "string",
      "description": "Why this decision was made (for decision type)"
    },
    "category": {
      "type": "string",
      "enum": ["technical", "business", "personal", "strategic"],
      "description": "Decision category (for decision type)"
    },
    "tags": {
      "type": "array",
      "items": {"type": "string"},
      "description": "Optional tags for categorization"
    },
    "metadata": {
      "type": "object",
      "description": "Additional metadata as key-value pairs"
    }
  },
  "required": ["type"]
}

{
  "type": "object",
  "properties": {
    "operation": {
      "type": "string",
      "description": "The operation to perform: list_active, create, complete, update_status, or delete"
    },
    "title": {
      "type": "string",
      "description": "Task title (for create operation)"
    },
    "description": {
      "type": "string",
      "description": "Task description (for create operation)"
    },
    "task_id": {
      "type": "string",
      "description": "Task ID (for complete, update_status, delete operations)"
    },
    "status": {
      "type": "string",
      "description": "New status (for update_status operation)"
    },
    "priority": {
      "type": "string",
      "description": "Task priority: low, medium, high, urgent (for create operation)"
    }
  },
  "required": ["operation"]
}

{
  "type": "object",
  "properties": {
    "operation": {
      "type": "string",
      "description": "Operation: log_decision, review_decisions, update_decision, analyze_patterns"
    },
    "title": {
      "type": "string",
      "description": "Decision title (for log_decision)"
    },
    "decision": {
      "type": "string",
      "description": "The decision made (for log_decision)"
    },
    "rationale": {
      "type": "string",
      "description": "Why this decision was made (for log_decision)"
    },
    "category": {
      "type": "string",
      "description": "Category: technical, business, personal, strategic (for log_decision)"
    },
    "decision_id": {
      "type": "string",
      "description": "Decision ID (for update_decision)"
    },
    "status": {
      "type": "string",
      "description": "New status: active, revisited, reversed, archived (for update_decision)"
    }
  },
  "required": ["operation"]
}

{
  "type": "object",
  "properties": {
    "query": {
      "type": "string",
      "description": "Natural language date query. Examples: 'January 9', 'tomorrow', 'today', 'Jan 9 2026', 'next Monday'. REQUIRED - always provide the date the user is asking about."
    },
    "date": {
      "type": "string",
      "description": "Specific date in YYYY-MM-DD format (e.g., '2026-01-09'). Use this OR query, not both."
    },
    "start": {
      "type": "string",
      "description": "Start datetime in ISO format for custom range (e.g., '2026-01-09T00:00:00-08:00')"
    },
    "end": {
      "type": "string",
      "description": "End datetime in ISO format for custom range (e.g., '2026-01-09T23:59:59-08:00')"
    }
  },
  "required": ["query"]
}

{
  "type": "object",
  "properties": {
    "operation": {
      "type": "string",
      "enum": ["create", "update", "delete"],
      "description": "Operation to perform: create, update, or delete"
    },
    "title": {
      "type": "string",
      "description": "Event title/summary (required for create, can be used with query/date for delete to search by name)"
    },
    "start": {
      "type": "string",
      "description": "Start datetime in ISO format (required for create)"
    },
    "end": {
      "type": "string",
      "description": "End datetime in ISO format (required for create)"
    },
    "description": {
      "type": "string",
      "description": "Event description/notes"
    },
    "location": {
      "type": "string",
      "description": "Event location"
    },
    "attendees": {
      "type": "array",
      "items": {"type": "string"},
      "description": "Email addresses of attendees"
    },
    "event_id": {
      "type": "string",
      "description": "Event ID (for update/delete - if not provided for delete, will search using query/date)"
    },
    "query": {
      "type": "string",
      "description": "Search query for delete operation (e.g., 'Daily Standup on January 9'). Use when event_id is unknown. Will search and delete matching event."
    },
    "date": {
      "type": "string",
      "description": "Date to search within for delete operation (e.g., 'January 9', '2026-01-09'). Used with query/title to find event before deleting."
    },
    "calendar_id": {
      "type": "string",
      "description": "Calendar ID (default: primary)"
    }
  },
  "required": ["operation"]
}

{
  "type": "object",
  "properties": {
    "operation": {
      "type": "string",
      "description": "Operation: update_milestone, check_progress, identify_risks"
    },
    "week": {
      "type": "number",
      "description": "Week number 1-4 (for update_milestone)"
    },
    "status": {
      "type": "string",
      "description": "New status: completed, in_progress, at_risk (for update_milestone)"
    },
    "milestone_id": {
      "type": "string",
      "description": "Specific milestone ID (for update_milestone)"
    }
  },
  "required": ["operation"]
}

{
  "type": "object",
  "properties": {
    "operation": {
      "type": "string",
      "enum": ["health_check", "validate_workflow", "get_failures", "find_bottlenecks", "backup_workflow", "detect_repeated_failures", "list_workflows"],
      "description": "Operation to perform"
    },
    "workflow_id": {
      "type": "string",
      "description": "Workflow ID (required for validate_workflow, find_bottlenecks, backup_workflow)"
    },
    "hours": {
      "type": "integer",
      "description": "Time range in hours (for get_failures)",
      "default": 24
    },
    "limit": {
      "type": "integer",
      "description": "Max results (for get_failures)",
      "default": 10
    },
    "reason": {
      "type": "string",
      "description": "Reason for backup (for backup_workflow)",
      "default": "ai-requested"
    }
  },
  "required": ["operation"]
}
